import React, { Component} from 'react';
import {Button, Form, Row, Container, Col} from 'react-bootstrap'
import axios from 'axios'

class Login extends Component{
    constructor(){
        super()
        this.state ={
            username: '', 
            password:'', 
            logedInName:'', 
            flag: false,
        }
    }
    changeHandler = (ev)=>{
        let nam = ev.target.name
        let val = ev.target.value
        this.setState({
            [nam]: val
        })
    }
    submit = (e)=>{
        e.preventDefault()
        axios.post('http://localhost:3001/login', this.state)
                .then((response)=>{
                   if(response.data.length){
                    this.setState({
                            logedInName: response.data[0].f_name.toUpperCase()+ ' ' + response.data[0].l_name.toUpperCase(),
                            flag: true
                            })  
                       setTimeout(()=>{
                        this.props.history.push('/')
                       }, 3000)
                   }else{
                     alert(response.data.message)

                    }
                })


    }
    render(){
        let loginStatus;
        // if(this.state.logedInName.lengthe > 2){
        // let loginStatus = <div class="jumbotron jumbotron-fluid">
        //                         <div class="container">
        //                          <h1 class="display-4">Welcome {this.state.logedInName}</h1>
        //                         </div>
        //                     </div>
        // }
        console.log(this.state.logedInName.lengthe)
        return (
            <>
                <Container className="my-5">
                    <Row>
                    <Col>{loginStatus}</Col>
                    <Col>
                        <Form>
                        <Form.Group controlId="formBasicEmail">
                        <Form.Label>User Name</Form.Label>
                        <Form.Control onChange={this.changeHandler} name='username' type="text" placeholder="User name" required/>
                        </Form.Group>

                        <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control onChange={this.changeHandler} name='password' type="password" placeholder="Password" />
                        </Form.Group>

                        <Button onClick={this.submit} variant="primary" type="submit">
                        Submit
                        </Button>
                        </Form>
                    </Col>
                    <Col></Col>
                    </Row>

                </Container>

            </>
        )}
}

export default Login
