import React, { Component} from 'react';
import {Button, Form, Row, Container, Col} from 'react-bootstrap'
import axios from 'axios'

class Login extends Component{
    constructor(){
        super()
        this.state ={
            username: '', 
            password:'', 
            logedIn:'', 
            lodedFailed:''
        }
    }
    changeHandler = (ev)=>{
        let nam = ev.target.name
        let val = ev.target.value
        this.setState({
            [nam]: val
        })
    }
    submit = (e)=>{
        e.preventDefault()
        axios.post('http://localhost:3001/login', this.state)
                .then((response)=>{
                   if(response.data.length){
                    this.setState({
                                loginStatus: response.data[0].f_name.toUpperCase()+ ' ' + response.data[0].l_name.toUpperCase()
                            })  
                       setTimeout(()=>{
                        this.props.history.push('/')
                       }, 2000)
                   }else{
                       this.setState({ 
                            lodedFailed: response.data.message
                       })
                    }
                })


    }
    render(){
        const loginStatus = <div class="alert alert-primary" role="alert">
        A simple primary alertâ€”check it out!
      </div>
        if(true){
            return loginStatus
        }else{
            return 
        }
        return (
            <>
                <Container className="my-5">
                    <Row>
                    <Col>{loginStatus}</Col>
                    <Col>
                        <Form>
                        <Form.Group controlId="formBasicEmail">
                        <Form.Label>User Name</Form.Label>
                        <Form.Control onChange={this.changeHandler} name='username' type="text" placeholder="User name" required/>
                        </Form.Group>

                        <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control onChange={this.changeHandler} name='password' type="password" placeholder="Password" />
                        </Form.Group>

                        <Button onClick={this.submit} variant="primary" type="submit">
                        Submit
                        </Button>
                        </Form>
                    </Col>
                    <Col></Col>
                    </Row>

                </Container>

            </>
        )}
}

export default Login
